function saliency_map_infra=GetHeatMap(saliency_map)
i_width_original=size(saliency_map,1);
i_length_original=size(saliency_map,2);
color_table=zeros(256,3);
    saliency_map_infra=zeros(i_width_original,i_length_original,3);
    for k=1:256/8 % 1~32
        color_table(k,1)=0.5+k/(256/4); % 0~1
    end
    for k=256/8+1:3*256/8 % 33~96
        color_table(k,1)=1;
        color_table(k,2)=0.0+(k-256/8)/(256/4); % 0~0.5
    end
    for k=3*256/8+1:5*256/8 % 97~160
        color_table(k,1)=1-(k-3*256/8)/(256/4); % 1~0
        color_table(k,2)=1.0;
        color_table(k,3)=1.0-color_table(k,1);
    end
    for k=5*256/8+1:7*256/8 % 161~224
        color_table(k,1)=0;
        color_table(k,2)=1.0-(k-5*256/8)/(256/4); % 1~0
        color_table(k,3)=1.0;
    end
    for k=7*256/8+1:256 % 225~256
        color_table(k,1)=0;
        color_table(k,2)=0;
        color_table(k,3)=1.0-(k-7*256/8)/(256/4); % 1~0.5
